import React, { useState } from "react";
import "./FeedbackForm.css";

const FeedbackForm = ({ closeFeedback }) => {
    const [rating, setRating] = useState(0);
    const [feedbackDetails, setFeedbackDetails] = useState("");
    const [suggestions, setSuggestions] = useState("");
    const [file, setFile] = useState(null);

    const handleRating = (value) => setRating(value);

    const handleFileChange = (event) => setFile(event.target.files[0]);

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log({
            rating,
            feedbackDetails,
            suggestions,
            file,
        });
        alert("Geri bildirim başarıyla gönderildi!");
        closeFeedback();
    };

    return (
        <div className="feedback-container">
            <div className="feedback-header">Etkinlik Geri Bildirimi</div>
            <div className="feedback-details">
                <p><strong>Etkinlik Detayları</strong></p>
                <p>Tarih: 5 Kasım 2024</p>
                <p>Saat: 14:00 - 15:00</p>
                <p>Durum: Tamamlandı</p>
            </div>
            <div className="rating">
                <strong>Oy</strong>
                <div>
                    {[...Array(5)].map((_, i) => (
                        <i
                            key={i}
                            className={`fas fa-star ${i < rating ? "text-warning" : "text-secondary"}`}
                            onClick={() => handleRating(i + 1)}
                        ></i>
                    ))}
                </div>
            </div>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Detaylı Geri Bildirim</label>
                    <textarea
                        className="form-control"
                        value={feedbackDetails}
                        onChange={(e) => setFeedbackDetails(e.target.value)}
                        placeholder="Detaylı geri bildiriminizi buraya yazın..."
                        rows="4"
                    ></textarea>
                </div>
                <div className="form-group">
                    <label>Öneriler</label>
                    <textarea
                        className="form-control"
                        value={suggestions}
                        onChange={(e) => setSuggestions(e.target.value)}
                        placeholder="Herhangi bir öneriniz var mı?"
                        rows="2"
                    ></textarea>
                </div>
                <div className="form-group">
                    <label>Ekler</label>
                    <input type="file" onChange={handleFileChange} />
                    <small>Kabul edilen dosya türleri: .jpg, .png, .pdf. Maksimum boyut: 5MB.</small>
                </div>
                <button type="submit" className="btn btn-primary mt-3">Submit Feedback</button>
            </form>
            <div className="back-link">
                <button onClick={closeFeedback} className="btn btn-link">Geri Bildirimlerime Dön</button>
            </div>
        </div>
    );
};

export default FeedbackForm;
