import React from "react";
import ReactDOM from "react-dom";
import ".counselorPages/Modal.css"; // Add specific styles for the modal

const Modal = ({ isOpen, onClose, onConfirm, selectedDate, selectedTimeRange }) => {
    if (!isOpen) return null;

    return ReactDOM.createPortal(
        <div className="modal-overlay">
            <div className="modal-content">
                <h3>Tercihlerinizi Onaylayın</h3>
                <p><strong>Tarih:</strong> {selectedDate?.toLocaleDateString("tr-TR")}</p>
                <p><strong>Saat:</strong> {selectedTimeRange}</p>
                <div className="modal-actions">
                    <button className="modal-confirm-button" onClick={onConfirm}>
                        Onayla
                    </button>
                    <button className="modal-cancel-button" onClick={onClose}>
                        İptal
                    </button>
                </div>
            </div>
        </div>,
        document.getElementById("modal-root") // Rendered into modal-root
    );
};

export default Modal;
