import React, { useState } from "react";
import Sidebar from "./counselorPages/Sidebar";
import Header from "./counselorPages/Header";
import CounselorDashboardContent from "./counselorPages/CounselorDashboardContent";
import "./App.css";

const App = () => {
    const [sidebarOpen, setSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    return (
        <div>
            <Header toggleSidebar={toggleSidebar} />
            <div className="d-flex">
                <Sidebar isOpen={sidebarOpen} />
                <div className={`content-container flex-grow-1 ${sidebarOpen ? "with-sidebar" : ""}`}>
                    <CounselorDashboardContent />
                </div>
            </div>
        </div>
    );
};

export default App;
