import React from "react";
import ReactDOM from "react-dom";

const Modal = ({ isOpen, onClose, onConfirm, selectedDate, selectedTimeRange }) => {
    if (!isOpen) return null;

    return ReactDOM.createPortal(
        <div
            style={{
                position: "fixed",
                top: 0,
                left: 0,
                width: "100vw",
                height: "100vh",
                backgroundColor: "rgba(255, 255, 255, 0.9)", // White with opacity
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                zIndex: 1050,
            }}
        >
            <div
                style={{
                    backgroundColor: "white",
                    padding: "20px",
                    borderRadius: "10px",
                    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
                    width: "400px",
                    textAlign: "center",
                }}
            >
                <h3 style={{ marginBottom: "15px" }}>Tercihlerinizi Onaylayın</h3>
                <p>
                    <strong>Tarih:</strong> {selectedDate?.toLocaleDateString("tr-TR")}
                </p>
                <p>
                    <strong>Saat:</strong> {selectedTimeRange}
                </p>
                <div style={{ marginTop: "20px" }}>
                    <button
                        style={{
                            backgroundColor: "#0A3981",
                            color: "white",
                            padding: "10px 20px",
                            borderRadius: "5px",
                            border: "none",
                            marginRight: "10px",
                            cursor: "pointer",
                        }}
                        onClick={onConfirm}
                    >
                        Onayla
                    </button>
                    <button
                        style={{
                            backgroundColor: "#d9d9d9",
                            color: "#5A5A5A",
                            padding: "10px 20px",
                            borderRadius: "5px",
                            border: "none",
                            cursor: "pointer",
                        }}
                        onClick={onClose}
                    >
                        İptal
                    </button>
                </div>
            </div>
        </div>,
        document.getElementById("modal-root")
    );
};

export default Modal;
