import React, { useState } from "react";
import Modal from "./Modal";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

const CreateTourApplication = () => {
    const [selectedDate, setSelectedDate] = useState(null);
    const [selectedTimeRange, setSelectedTimeRange] = useState("");
    const [isModalOpen, setModalOpen] = useState(false);

    const timeRanges = ["09:00-11:00", "11:00-13:00", "14:00-16:00", "16:30-18:30"];

    const handleDateChange = (date) => {
        setSelectedDate(date);
        setSelectedTimeRange("");
    };

    const handleTimeChange = (e) => {
        setSelectedTimeRange(e.target.value);
    };

    const handleSelect = () => {
        if (!selectedDate || !selectedTimeRange) {
            alert("Lütfen bir tarih ve saat aralığı seçin!");
            return;
        }
        setModalOpen(true);
    };

    const handleConfirm = () => {
        setModalOpen(false);
        alert(
            `Başvurunuz onaylandı!\nTarih: ${selectedDate?.toLocaleDateString(
                "tr-TR"
            )}\nSaat: ${selectedTimeRange}`
        );
    };

    return (
        <div className="tour-application-container">
            <h2>Tur Başvurusu Yap</h2>
            <div className="tour-application-wrapper">
                <div className="calendar-section">
                    <label>Bir Tarih Seçiniz:</label>
                    <Calendar onChange={handleDateChange} value={selectedDate} locale="tr-TR" />
                </div>

                <div className="time-selection-section">
                    <label>Tur İçin Saat Aralığı Seçiniz:</label>
                    <select
                        value={selectedTimeRange}
                        onChange={handleTimeChange}
                        className="time-dropdown"
                    >
                        <option value="">Saat Aralığı Seç</option>
                        {timeRanges.map((range, index) => (
                            <option key={index} value={range}>
                                {range}
                            </option>
                        ))}
                    </select>
                    <button className="select-button" onClick={handleSelect}>
                        Seç
                    </button>
                </div>
            </div>

            <Modal
                isOpen={isModalOpen}
                onClose={() => setModalOpen(false)}
                onConfirm={handleConfirm}
                selectedDate={selectedDate}
                selectedTimeRange={selectedTimeRange}
            />
        </div>
    );
};

export default CreateTourApplication;
