import React from "react";
import { format, getDaysInMonth } from "date-fns";
import { tr } from "date-fns/locale";
import "./PuantageTable.css";

const PuantageTable = ({ users }) => {
  const [selectedMonth, setSelectedMonth] = React.useState(new Date());
  const daysInMonth = getDaysInMonth(selectedMonth);
  const formatDate = (date) => format(date, "yyyy-MM-dd");

  return (
    <div className="calendar-container">
      <div className="month-selector">
        <button
          onClick={() =>
            setSelectedMonth(
              new Date(selectedMonth.setMonth(selectedMonth.getMonth() - 1))
            )
          }
        >
          Önceki Ay
        </button>
        <span>{format(selectedMonth, "MMMM yyyy", { locale: tr })}</span>
        <button
          onClick={() =>
            setSelectedMonth(
              new Date(selectedMonth.setMonth(selectedMonth.getMonth() + 1))
            )
          }
        >
          Sonraki Ay
        </button>
      </div>

      <div className="table-wrapper">
        <table className="calendar-table">
          <thead>
            <tr>
              <th>İsim/Gün</th>
              {Array.from({ length: daysInMonth }, (_, i) => (
                <th key={i + 1}>{i + 1}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.firstName} {user.lastName}</td>
                {Array.from({ length: daysInMonth }, (_, i) => {
                  const day = formatDate(
                    new Date(selectedMonth.getFullYear(), selectedMonth.getMonth(), i + 1)
                  );
                  const assignedTour = user.assignedTours?.find(
                    (tour) => tour.chosenDate === day
                  );

                  return (
                    <td key={i}>
                      {assignedTour ? (
                        <div className="activity-box">
                          <span>{assignedTour.workHours} hours</span>
                          <div className="tooltip">
                            <div>High School: {assignedTour.applyingHighschool?.name}</div>
                            <div>Visitors: {assignedTour.visitorCount}</div>
                          </div>
                        </div>
                      ) : (
                        <span>–</span>
                      )}
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PuantageTable;
