import React, { useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./CreateTourApplication.css";

const timeSlots = [
    { id: "SLOT_9_11", displayName: "09:00-11:00" },
    { id: "SLOT_11_13", displayName: "11:00-13:00" },
    { id: "SLOT_13_15", displayName: "13:00-15:00" },
    { id: "SLOT_15_17", displayName: "15:00-17:00" },
];

const CreateTourApplication = () => {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [selectedTimeSlot, setSelectedTimeSlot] = useState("");
    const [confirmed, setConfirmed] = useState(false);

    const handleTimeSlotChange = (e) => {
        setSelectedTimeSlot(e.target.value);
        setConfirmed(false);
    };

    const handleConfirm = () => {
        if (selectedDate && selectedTimeSlot) {
            setConfirmed(true);
        } else {
            alert("Lütfen bir tarih ve saat aralığı seçin!");
        }
    };

    const formatDate = (date) => {
        return new Intl.DateTimeFormat("tr-TR", {
            day: "2-digit",
            month: "long",
            year: "numeric",
        }).format(date);
    };

    return (
        <div className="tour-application-container">
            <h2>Tur Başvurusu Yap</h2>
            <div className="tour-application-wrapper">
                {/* Calendar Section */}
                <div className="calendar-section">
                    <label>Bir Tarih Seçiniz:</label>
                    <Calendar
                        onChange={setSelectedDate}
                        value={selectedDate}
                        locale="tr-TR"
                        tileClassName={({ date, view }) => {
                            // Example: Highlight today
                            if (date.toDateString() === new Date().toDateString()) {
                                return "highlight-today";
                            }
                            return null;
                        }}
                    />
                </div>

                {/* Time Selection Section */}
                <div className="time-selection-section">
                    <label>Tur İçin Saat Aralığı Seçiniz:</label>
                    <select
                        value={selectedTimeSlot}
                        onChange={handleTimeSlotChange}
                        className="time-dropdown"
                    >
                        <option value="">Saat Aralığı Seç</option>
                        {timeSlots.map((slot) => (
                            <option key={slot.id} value={slot.id}>
                                {slot.displayName}
                            </option>
                        ))}
                    </select>
                    <button className="select-button" onClick={handleConfirm}>
                        Seç
                    </button>
                </div>
            </div>

            {/* Summary Section */}
            <div className="summary-section">
                <h3>Zaman Tercihiniz:</h3>
                {confirmed && selectedDate && selectedTimeSlot ? (
                    <div className="confirmation-box">
                        <p>Tarih: {formatDate(selectedDate)}</p>
                        <p>
                            Saat:{" "}
                            {
                                timeSlots.find((slot) => slot.id === selectedTimeSlot)
                                    ?.displayName
                            }
                        </p>
                        <button className="confirm-button">Tercihleri Onayla</button>
                    </div>
                ) : (
                    <p className="no-selection">Henüz bir tercih yapılmadı.</p>
                )}
            </div>
        </div>
    );
};

export default CreateTourApplication;
